function getActivities(){$.post({url:"/api/get-activities",data:{userAuthToken:userAuthToken},success:function(t){displayActivities(t)},error:function(t,e,i){console.error("Error: "+e+" "+i)}})}function displayActivities(t){let e=JSON.parse(t),i="<p>You have not added any activities yet.</p>";if(0!=e.length){i="<table class='table table-striped'><thead><tr><th>Activity</th><th>Subject</th><th>Actions</th></tr></thead><tbody>";for(let t=0;t<e.length;t++){let a=new Date(e[t].added_date).toLocaleDateString(),n=e[t].id;i+="<tr id='activity_"+n+"'><td>"+e[t].activity+"<br><small class='text-muted'>Added "+a+"</small></td><td>"+e[t].subject+"</td><td><a href='/activity/"+n+"' class='btn btn-sm btn-outline-secondary'>View</a> <button class='btn btn-sm btn-outline-danger delete-user-activity' data-id='"+n+"'>Delete</button></td></tr>"}i+="</tbody></table>"}$("#activity-list").html(i)}function deleteActivity(t){confirm("Are you sure you want to delete this activity?")&&$.post({url:"/api/delete-activity",data:{id:t,userAuthToken:userAuthToken},success:function(e){$("#activity_"+t).remove()},error:function(t,e,i){console.error("Error: "+e+" "+i)}})}getActivities(),$(document).ready(function(){$(document).on("click",".delete-user-activity",function(){let t=$(this).data("id");deleteActivity(t)})});